mapper.select:
SELECT MID, PWD, MNAME, EMAIL, PHONENUM
FROM MEMBER
WHERE MID= #{id}
AND
PWD = #{password}

SELECT MID
FROM MEMBER
WHERE MNAME= #{name} 
AND EMAIL = #{email}

SELECT MID
FROM MEMBER
WHERE 
MID= #{id} 
AND 
MNAME = #{name}

SELECT MEMBER.RNUM, MEMBER.MID, MEMBER.MNAME, MEMBER.EMAIL,	MEMBER.PHONENUM
FROM ( SELECT ROWNUM RNUM, M.MID, M.MNAME, M.EMAIL,	M.PHONENUM
FROM ( SELECT MID, MNAME, EMAIL, PHONENUM
FROM MEMBER

) M
) MEMBER
WHERE MEMBER.RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE MEMBER
MID = #{id}, PWD = #{password}, MNAME = #{name}, EMAIL = #{email}, PHONENUM = #{phonenum}
WHERE MID = #{id}

